"use strict";var list=[];function getList(){$.ajax({url:"../json/data.json",dataType:"json",success:function(a){console.log(a),list=a,$(".pagi").pagination({pageCount:Math.ceil(a.length/28),current:1,jump:!0,coping:!0,homePage:"首页",endPage:"末页",prevContent:"上页",nextContent:"下页",callback:function(n){var i=n.getCurrent();bindHtml(a.slice(28*(i-1),28*i))}}),bindHtml(a.slice(0,28))}})}function bindHtml(n){console.log(n);var i="";n.forEach(function(n){i+='\n        <li data-id="'.concat(n.id,'">\n            <div class="pic">\n              <a href="javascript:void(0)">\n                <img src="').concat(n.img,'" alt="">\n              </a>\n            </div>\n            <div class="ctx-box">\n              <div class="row row-1 clearfix">\n                <div class="price">\n                  <span>￥</span>\n                  <strong>').concat(n.price,'</strong>\n                  <span class="deal-cnt">16人付款</span>\n                </div>\n              </div>\n              <div class="row row-2 title">\n                <a href="javascript:void(0)">\n                  <span class="H">乐视</span>\n                  ').concat(n.name,'\n                </a>\n              </div>\n              <div class="row row-3 g-clearfix">\n                <div class="shop">').concat(n.shop,'</div>\n                <div class="location">').concat(n.location,"</div>\n              </div>\n            </div>\n          </li>\n        ")}),$(".grid-wrap>ul").html(i)}getList(),$(".grid-wrap>ul").on("click","li",function(){console.log(this);var n=$(this).data("id");console.log(n);for(var i=null,a=0;a<list.length;a++)if(list[a].id==n){i=list[a];break}localStorage.setItem("goodsInfo",JSON.stringify(i)),window.location.href="./detail.html"});