"use strict";var cards=document.querySelectorAll(".card"),btns=document.querySelectorAll(".js-btn");function on_btn_click(t){var c=t.currentTarget.getAttribute("data-target"),e=document.getElementById(c);if(e)return bg_change(c),view_change(e),!1}function bg_change(t){document.body.className="",document.body.classList.add("is-"+t)}function view_change(t){cards.forEach(function(t){t.classList.remove("is-show")}),t.classList.add("is-show")}btns.forEach(function(t){t.addEventListener("click",on_btn_click,!0),t.addEventListener("touch",on_btn_click,!0)});